    .catch(err => {console.log(err);
    });
  }
  handleSubmit(event){
    this.setState(
      {
        player1: 'player 1',
        player2: 'player 2'
      }
    )
  }
  render (){

    if (this.state.game == null) {
      return (
        <div>
          <h1>Press the button to start the game.</h1>
          <button onClick={this.startGame}>Start game</button>
        </div>
      )
    }
    return(
      <div>
        {this.state.player1}
      </div>
    )
  }

}

export default App;
