import { Component } from 'react';
import './App.css';
import { Api } from './components/Api';
import TicTacToe from './components/tictactoe';


export class App extends Component {
  api = {}

  constructor(props) {
    super(props);
    this.api = new Api();
    this.state = {
      player1: "",
      player2: "",
      game: null
    }
  }

  handlePlayer1(event){
    this.setState({player1: event.target.value})
    console.log(event)
  }

  render (){

    if (this.state.player1 == "") {
      return (
        <div>
          <form onSubmit={this.handlePlayer1}>
            <label>Please enter player 1's name
              <input type="text"/>
            </label>
            <input type="submit" value="Submit"/>
          </form>

        </div>
      )
    }
    return(
      <div>
        {this.state.player1}
      </div>
    )
  }

}

export default App;
