import Grid from "./grid";

export class TicTacToe extends Component {
    constructor(props) {
      super(props);
      this.state = {
        gridArray: props.board,
        firstPlayerTurn: true
      }
  
      this.gamePlay = this.gamePlay.bind(this);
    }

    
    onTilePress = (index) => {
      console.log(props.gameId)
        // await this.gamePlay(isFirstPlayerTurn ? 0 : 1, index[0], index[1], props.gameId)
        
        console.log(this.state.firstPlayerTurn)
        this.setState(
          {firstPlayerTurn: !firstPlayerTurn}
        )
        console.log(this.state.firstPlayerTurn)
    }
    render(){
      return(
        <div>
          <Grid
          gridArray={gridArray}
          onTilePress={onTilePress}
          />
        </div>
        )
      }
}

export default TicTacToe;
