import { Component } from 'react';
import './App.css';
import { Api } from './components/Api';
import TicTacToe from './components/tictactoe';


export class App extends Component {
  api = {}

  constructor(props) {
    super(props);
    this.api = new Api();
    this.state = {
      game: null
    }
  }

  async startGame(){
    console.log('I was clicked')
    await this.api.startGame().then((responseJson) => {
      if (responseJson) {
        this.setState({
          game: responseJson
        });
      } else {
        console.warn("Credentials incorrect");
      }
      return null;
    })
    .catch((error) => {
      console.log("ERROR")
    });
  }
  handleSubmit(event){
    this.setState(
      {
        player1: 'player 1',
        player2: 'player 2'
      }
    )
  }
  render (){

    if (this.state.game == null) {
      return (
        <div>
          <h1>Press the button to start the game.</h1>
          <button onClick={this.startGame}>Start game</button>
        </div>
      )
    }
    return(
      <div>
        {this.state.player1}
      </div>
    )
  }

}

export default App;
