    constructor(props) {
      super(props);
      this.state = {
        gridArray: props.board,
        gameId: props.gameId,
        firstPlayerTurn: true
      }
  
      this.gamePlay = this.gamePlay.bind(this);
    }

    
    onTilePress(index){
      console.log(this.state.gameId)
